sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Business rule
function onAfter(current, previous) {
  // Get the group name value from excel sheet.
  var group = GlideRecord('sys_user_group');
  group.get('name', 'Assignmentgroup', 'Personagroup'); //assignment- and personagroup need to be replaced with group name value from excel spreadsheet.
  // Add the new user to the group.
  var groupMember = GlideRecord('sys_user_grmember');
  groupMember.initialize();
  groupMember.user = current.sys_id;
  groupMember.group = group.sys_id;
  groupMember.insert();
}

})(current, previous);
