sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onAfter*/ ) {

	// Add your code here
	// get the user
    var user = new GlideRecord('sys_user');
    user.addQuery('user_name',source.u_username__cid_.toString());
	user.query(); 
	user.next();

	// get the assignment group
    var assignmentgroup = new GlideRecord('sys_user_group');
    assignmentgroup.addQuery('name',source.u_user_group.toString());
	assignmentgroup.query(); 
	assignmentgroup.next();

	insertUser(user, assignmentgroup);
	var personagroup = new GlideRecord('sys_user_group');
    personagroup.addQuery('name',source.u_persona_group.toString());
	personagroup.query(); 
	personagroup.next();
	insertUser(user, personagroup);

	function insertUser(user, group) {

		// insert group membership
		var grMember = new GlideRecord('sys_user_grmember');
		grMember.initialize();
		grMember.user = user.sys_id;
		grMember.group = group.sys_id;
		grMember.insert();

	} 
})(source, map, log, target);
