sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onAfter*/ ) {

	// Add your code here
	// get the user
    var user = new GlideRecord('sys_user');
    user.addQuery('user_name',source.u_username__cid_.toString());
	user.query();
	if(!user.hasNext()) 
		return;
	user.next();

	let userGroup = source.u_user_group.toString();
	let propertyName = "PERSONA.";
	if (!userGroup) return;

	propertyName += userGroup.toString();

	// check if there already is a set property name in the "connection"-table of "assignme_connection"
	let check = new GlideRecord('x_1138257_assignme_connection');
	check.addQuery("name", propertyName);
	check.query();

	// if it has a propertyname already the function ends
	if(!check.hasNext())
		return;
	check.next();

	let personaGroupId = JSON.parse(check.json);
	personaGroupId = personaGroupId.personaGroups[0];

	// get the persona group
	var personagroup = new GlideRecord('sys_user_group');
    personagroup.addQuery('sys_id', personaGroupId.toString());
	personagroup.query();
	if(!personagroup.hasNext()) 
		return;
	personagroup.next();
	insertUser(user, personagroup);

	function insertUser(user, group) {

		// insert group membership
		var grMember = new GlideRecord('sys_user_grmember');
		grMember.initialize();
		grMember.user = user.sys_id;
		grMember.group = group.sys_id;
		grMember.insert();

	}
	//curent state of being: this script creates an empty user in the targeted persona group.
})(source, map, log, target);
